import React, { useEffect, useState } from 'react';
import './terminal.css';

export const Terminal = ({ listOfThreads }) => {
    const [operatingSystem, setOperatingSystem] = useState('');
    const [userHatice, setUserHatice] = useState('');
    const [currentDirectory, setCurrentDirectory] = useState('');

  useEffect(() => {
    fetch('/get_os')
      .then(response => response.json())
      .then(data => {
        setOperatingSystem(data.operating_system);
        setUserHatice(data.user_hatice);
        setCurrentDirectory(data.current_directory);
      })
      .catch(error => {
        console.error('Hata:', error);
      });
  }, []);

    return (
        <>
            <div className='terminal-container'>
                <div className='terminal-output'>
                    {listOfThreads.map(thread => {
                        return (
                            <ul key={thread.id}>
                                <li>{userHatice}@{operatingSystem}:{currentDirectory}$ {thread.command}</li>
                                <li>{thread.output}</li>
                            </ul>
                        )
                    })}
                </div>
            </div>
        </>
    )
}
