import React, { useState, useEffect } from "react";
import { Terminal } from "../Components/Terminal/terminal";
import { Form } from "../Components/Form/form";

export const ThreadPage = () => {
    const [thread, setThread] = useState([]);
    const [addThread, setAddThread] = useState('');

    const fetchData = async (url, successCallback) => {
        try {
            const response = await fetch(url);

            if (response.ok) {
                const data = await response.json();
                successCallback(data);
            }
        } catch (error) {
            console.error('Fetch error:', error);
        }
    };

    const getLatestThreads = () => {
        fetchData('/thread', data => setThread(data));
    };

    const getLatestPromptDirectory = () => {
        fetchData('/get_os', data => setPromptDirectory(data.prompt_directory));
    };

    const handleFormChange = (inputValue) => {
        setAddThread(inputValue);
    };

    const handleFormSubmit = () => {
        fetchData('/thread/create', responseData => {
            setAddThread('');
            if (addThread !== 'clear') {
                getLatestThreads();
            } else {
                setThread([]);
            }
            getLatestPromptDirectory();
        });
    };

    useEffect(() => {
        getLatestThreads();
    }, []);

    return (
        <div>
            <Form userInput={addThread} onFormChange={handleFormChange} onFormSubmit={handleFormSubmit} />
            <Terminal listOfThreads={thread} />
        </div>
    );
};
