import React, { useState, useEffect } from "react";
import { Terminal } from "../Components/Terminal/terminal";
import { Form } from "../Components/Form/form";

export const ThreadPage = () => {

    const [thread, setThread] = useState([]);
    const [addThread, setAddThread] = useState('');
    const [prompt, setPrompt] = useState('');

    useEffect(() => {
        fetch('/thread')
        .then(response => {
            if (response.ok) {
                return response.json()
            }
        }).then(data => setThread(data))
    }, [])

    useEffect(() => {
        fetch('/get_prompt')
            .then(response => response.json())
            .then(data => setPrompt(data.prompt))
            .catch(error => {
                console.log(error);
            });
    }, []);

    const handleFormChange = (inputValue) => {
        setAddThread(inputValue);
    }

    const handleFormSubmit = () => {
        fetch('/thread/create', {
            method: 'POST',
            body: JSON.stringify({
                command: addThread
            }),
            headers: {
                'Content-Type': 'application/json; charset=UTF-8'
            }
        }).then(response => response.json())
            .then(responseData => {
                console.log(responseData)
                setAddThread('')
                getLatestThreads()
            })

    }

    const getLatestThreads = () => {
        fetch('/thread').then(response => {
            if (response.ok) {
                return response.json()
            }
        }).then(data => setThread(data))
    }

    return (
        <div>
            <Form userInput={addThread} onFormChange={handleFormChange} onFormSubmit={handleFormSubmit} />
            <Terminal listOfThreads={thread} />
        </div>
    );
};
