import React, { useEffect, useState } from 'react';
import './terminal.css';

export const Terminal = ({ listOfThreads }) => {
    const [promptDirectory, setPromptDirectory] = useState('');
    const [commandOutput, setCommandOutput] = useState({});

    useEffect(() => {
        // Redis'ten komut çıktılarını alın
        if (listOfThreads && listOfThreads.length > 0) {
            listOfThreads.forEach(thread => {
                fetch(`/thread/output/${thread.command}`)
                    .then(response => response.json())
                    .then(data => {
                        setCommandOutput(prevOutput => ({
                            ...prevOutput,
                            [thread.command]: data.output,
                        }));
                    })
                    .catch(error => {
                        console.error('Error:', error);
                    });
            });
        }
    }, [listOfThreads]);

    return (
        <>
            <div className='terminal-container'>
                <div className='terminal-output'>
                    {listOfThreads.map(thread => {
                        return (
                            <ul key={thread.id}>
                                <li>{promptDirectory} {thread.command}</li>
                                <li>{commandOutput[thread.command]}</li>
                            </ul>
                        )
                    })}
                </div>
            </div>
        </>
    )
}
