import React, { useState, useEffect } from "react";
import { Terminal } from "../Components/Terminal/terminal";
import { Form } from "../Components/Form/form";

export const ThreadPage = () => {
    const [thread, setThread] = useState([]);
    const [addThread, setAddThread] = useState('');
    const [promptDirectory, setPromptDirectory] = useState('');

    useEffect(() => {
        // Sayfa yüklendiğinde localStorage'dan verileri al
        const storedThreads = localStorage.getItem('threads');
        if (storedThreads) {
            setThread(JSON.parse(storedThreads));
        }

        fetch('/thread')
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
            })
            .then(data => {
                setThread(data);
                // Verileri localStorage'a kaydet
                localStorage.setItem('threads', JSON.stringify(data));
            });
    }, []);

    useEffect(() => {
        fetch('/get_os')
            .then(response => response.json())
            .then(data => {
                setPromptDirectory(data.prompt_directory);
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }, []);

    const handleFormChange = (inputValue) => {
        setAddThread(inputValue);
        if (inputValue === 'clear') {
            setThread([]);
            // Temizlendikten sonra localStorage'dan da kaldır
            localStorage.removeItem('threads');
        }
    }

    const handleFormSubmit = () => {
        fetch('/thread/create', {
            method: 'POST',
            body: JSON.stringify({
                command: addThread
            }),
            headers: {
                'Content-Type': 'application/json; charset=UTF-8'
            }
        })
            .then(response => response.json())
            .then(responseData => {
                setAddThread('');
                getLatestThreads();
                getLatestPromptDirectory();
            })

    }

    const getLatestThreads = () => {
        fetch('/thread')
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
            })
            .then(data => {
                setThread(data);
                // Verileri localStorage'a kaydet
                localStorage.setItem('threads', JSON.stringify(data));
            });
    }

    const getLatestPromptDirectory = () => {
        fetch('/get_os')
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
            })
            .then(data => setPromptDirectory(data.prompt_directory));
    }

    return (
        <div>
            <Form userInput={addThread} onFormChange={handleFormChange} onFormSubmit={handleFormSubmit} />
            <Terminal listOfThreads={thread} />
        </div>
    );
};
