import React, { useEffect, useState } from 'react';
import './terminal.css';

export const Terminal = ({ listOfThreads }) => {
    const [promptDirectory, setPromptDirectory] = useState('');

    useEffect(() => {
        // Sayfa yüklendiğinde localStorage'dan `listOfThreads`'ü alabilirsiniz.
        const storedThreads = localStorage.getItem('listOfThreads');
        if (storedThreads) {
            setListofThreads(JSON.parse(storedThreads));
        }

        fetch('/get_os')
            .then(response => response.json())
            .then(data => {
                setPromptDirectory(data.prompt_directory);
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }, []);

    // Her `listOfThreads` değiştiğinde, localStorage'a güncel değeri kaydedebilirsiniz.
    useEffect(() => {
        localStorage.setItem('listOfThreads', JSON.stringify(listOfThreads));
    }, [listOfThreads]);

    return (
        <>
            <div className='terminal-container'>
                <div className='terminal-output'>
                    {listOfThreads.map(thread => {
                        return (
                            <ul key={thread.id}>
                                <li>{promptDirectory} {thread.command}</li>
                                <li>{thread.output}</li>
                            </ul>
                        )
                    })}
                </div>
            </div>
        </>
    )
}
