import './terminal.css';

export const Terminal = ({ listOfThreads }) => {
    const [promptDirectory, setPromptDirectory] = useState('');
    const [lastDirectory, setLastDirectory] = useState('');

    useEffect(() => {
        fetch('/get_os')
            .then(response => response.json())
            .then(data => {
                setPromptDirectory(data.prompt_directory);
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }, [listOfThreads]);

    useEffect(() => {
        fetch('/thread/create', {
            method: 'POST',
            body: JSON.stringify({
                last_directory: lastDirectory
            }),
